import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './ui/Card';
import { Progress } from './ui/Progress';
import { CheckCircle, AlertTriangle, Clock, FileText, Calendar, TrendingUp } from 'lucide-react';

const ReportPage = () => {
  // 模拟合规报告数据
  const complianceData = {
    overall: {
      score: 92,
      status: 'excellent',
      lastUpdate: '2024-01-15'
    },
    requirements: [
      {
        id: 1,
        name: '必修课程完成度',
        required: 10,
        completed: 9,
        percentage: 90,
        status: 'good',
        deadline: '2024-03-31'
      },
      {
        id: 2,
        name: '安全培训认证',
        required: 4,
        completed: 4,
        percentage: 100,
        status: 'excellent',
