import React, { useState } from 'react';
import { User, Lock, UserCheck, GraduationCap } from 'lucide-react';

const LoginPage = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    userType: 'student' // 'admin' or 'student'
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  // Ê®°ÊãüÁî®Êà∑Êï∞ÊçÆ
  const users = {
    admin: {
      username: 'admin',
      password: 'admin123',
      role: 'admin',
      name: 'Á≥ªÁªüÁÆ°ÁêÜÂëò'
    },
    student: {
      username: 'student',
      password: 'student123', 
      role: 'student',
      name: 'Â≠¶ÂëòÁî®Êà∑'
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setError(''); // Ê∏ÖÈô§ÈîôËØØ‰ø°ÊÅØ
  };

  const handleUserTypeChange = (type) => {
    setFormData(prev => ({
      ...prev,
      userType: type,
      username: '',
      password: ''
    }));
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    // ÁÆÄÂåñÁôªÂΩïÈ™åËØÅ - ‰ªªÊÑèË¥¶Âè∑ÂØÜÁ†ÅÈÉΩÂèØ‰ª•ÁôªÂΩï
    setTimeout(() => {
      // Âè™Ë¶ÅÁî®Êà∑ÂêçÂíåÂØÜÁ†Å‰∏ç‰∏∫Á©∫Â∞±ÂÖÅËÆ∏ÁôªÂΩï
      if (formData.username.trim() && formData.password.trim()) {
        // ÁôªÂΩïÊàêÂäü
        const userInfo = {
          username: formData.username,
          role: formData.userType, // ‰ΩøÁî®ÈÄâÊã©ÁöÑÁî®Êà∑Á±ªÂûã
          name: formData.userType === 'admin' ? 'ÁÆ°ÁêÜÂëò' : 'Â≠¶Âëò',
          loginTime: new Date().toISOString()
        };

        // ‰øùÂ≠òÂà∞localStorage
        localStorage.setItem('userInfo', JSON.stringify(userInfo));

        // Ë∞ÉÁî®Áà∂ÁªÑ‰ª∂ÁöÑÁôªÂΩïÂõûË∞É
        onLogin(userInfo);
      } else {
        setError('ËØ∑ËæìÂÖ•Áî®Êà∑ÂêçÂíåÂØÜÁ†Å');
      }

      setIsLoading(false);
    }, 500); // ÂáèÂ∞ëÁ≠âÂæÖÊó∂Èó¥
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="max-w-md w-full bg-white rounded-2xl shadow-xl p-8">
        {/* LogoÂíåÊ†áÈ¢ò */}
        <div className="text-center mb-8">
          <div className="mx-auto w-16 h-16 bg-blue-600 rounded-full flex items-center justify-center mb-4">
            <GraduationCap className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-2xl font-bold text-gray-900 mb-2">Êô∫ËÉΩÊâãÂÜåÂ≠¶‰π†Á≥ªÁªü</h1>
          <p className="text-gray-600">ËØ∑ÈÄâÊã©Ë∫´‰ªΩÁ±ªÂûãÂπ∂ÁôªÂΩï</p>
        </div>

        {/* Ë∫´‰ªΩÁ±ªÂûãÈÄâÊã© */}
        <div className="mb-6">
          <div className="flex rounded-lg bg-gray-100 p-1">
            <button
              type="button"
              onClick={() => handleUserTypeChange('student')}
              className={`flex-1 flex items-center justify-center py-2 px-4 rounded-md text-sm font-medium transition-colors ${
                formData.userType === 'student'
                  ? 'bg-white text-blue-600 shadow-sm'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              <GraduationCap className="w-4 h-4 mr-2" />
              Â≠¶ÂëòÁôªÂΩï
            </button>
            <button
              type="button"
              onClick={() => handleUserTypeChange('admin')}
              className={`flex-1 flex items-center justify-center py-2 px-4 rounded-md text-sm font-medium transition-colors ${
                formData.userType === 'admin'
                  ? 'bg-white text-blue-600 shadow-sm'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              <UserCheck className="w-4 h-4 mr-2" />
              ÁÆ°ÁêÜÂëòÁôªÂΩï
            </button>
          </div>
        </div>

        {/* ÂäüËÉΩÊèêÁ§∫ */}
        <div className="mb-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
          <div className="flex items-start space-x-3">
            <div className="flex-shrink-0">
              <div className="w-6 h-6 bg-blue-100 rounded-full flex items-center justify-center">
                <span className="text-blue-600 text-sm font-medium">üí°</span>
              </div>
            </div>
            <div>
              <p className="text-blue-800 text-sm font-medium mb-1">ÂäüËÉΩ‰ΩìÈ™åÊèêÁ§∫</p>
              <p className="text-blue-700 text-sm">
                ËØ∑Âú®ÁÆ°ÁêÜÂëòÊ®°ÂºèÁôªÂΩïdemoÊµèËßàÂÆåÊï¥ÂäüËÉΩ
              </p>
            </div>
          </div>
        </div>

        {/* ÁôªÂΩïË°®Âçï */}
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Áî®Êà∑ÂêçËæìÂÖ• */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Áî®Êà∑Âêç
            </label>
            <div className="relative">
              <User className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
              <input
                type="text"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-colors"
                placeholder={formData.userType === 'admin' ? 'ËØ∑ËæìÂÖ•ÁÆ°ÁêÜÂëòÁî®Êà∑Âêç' : 'ËØ∑ËæìÂÖ•Â≠¶ÂëòÁî®Êà∑Âêç'}
                required
              />
            </div>
          </div>

          {/* ÂØÜÁ†ÅËæìÂÖ• */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              ÂØÜÁ†Å
            </label>
            <div className="relative">
              <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-colors"
                placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å"
                required
              />
            </div>
          </div>

          {/* ÈîôËØØ‰ø°ÊÅØ */}
          {error && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-3">
              <p className="text-red-600 text-sm">{error}</p>
            </div>
          )}

          {/* ÁôªÂΩïÊåâÈíÆ */}
          <button
            type="submit"
            disabled={isLoading}
            className={`w-full py-3 px-4 rounded-lg font-medium transition-colors ${
              isLoading
                ? 'bg-gray-400 cursor-not-allowed'
                : 'bg-blue-600 hover:bg-blue-700 focus:ring-2 focus:ring-blue-500 focus:ring-offset-2'
            } text-white`}
          >
            {isLoading ? 'ÁôªÂΩï‰∏≠...' : 'ÁôªÂΩï'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
