import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './ui/Card';
import { Button } from './ui/Button';
import { CheckCircle, XCircle } from 'lucide-react';

const QuizPage = () => {
  // 模拟题目数据
  const [questions] = useState([
    {
      id: 1,
      question: '以下哪个是React的核心概念？',
      type: 'single',
      options: [
        { id: 'a', text: '组件化' },
        { id: 'b', text: '虚拟DOM' },
        { id: 'c', text: '单向数据流' },
        { id: 'd', text: '以上都是' }
      ],
      correctAnswer: 'd',
      explanation: 'React的核心概念包括组件化、虚拟DOM和单向数据流，这些都是React框架的重要特性。'
    },
    {
      id: 2,
      question: '以下哪些是JavaScript的数据类型？（多选）',
      type: 'multiple',
      options: [
        { id: 'a', text: 'String' },
        { id: 'b', text: 'Number' },
        { id: 'c', text: 'Boolean' },
        { id: 'd', text: 'Array' }
      ],
      correctAnswer: ['a', 'b', 'c'],
      explanation: 'JavaScript的基本数据类型包括String、Number、Boolean等，而Array是引用类型，不是基本数据类型。'
    },
    {
      id: 3,
      question: 'CSS中用于设置元素浮动的属性是？',
      type: 'single',
      options: [
        { id: 'a', text: 'position' },
        { id: 'b', text: 'float' },
        { id: 'c', text: 'display' },
        { id: 'd', text: 'clear' }
      ],
      correctAnswer: 'b',
      explanation: 'CSS中的float属性用于设置元素的浮动，可以让元素脱离正常的文档流。'
    }
  ]);

  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);

  const handleAnswerChange = (questionId, optionId, isMultiple = false) => {
    if (submitted) return;

    setAnswers(prev => {
      if (isMultiple) {
        const currentAnswers = prev[questionId] || [];
        const newAnswers = currentAnswers.includes(optionId)
          ? currentAnswers.filter(id => id !== optionId)
          : [...currentAnswers, optionId];
        return { ...prev, [questionId]: newAnswers };
      } else {
        return { ...prev, [questionId]: optionId };
      }
    });
  };

  const handleSubmit = () => {
    setSubmitted(true);
  };

  const isCorrectAnswer = (question, userAnswer) => {
    if (question.type === 'multiple') {
      const correctSet = new Set(question.correctAnswer);
      const userSet = new Set(userAnswer || []);
      return correctSet.size === userSet.size && 
             [...correctSet].every(x => userSet.has(x));
    } else {
      return question.correctAnswer === userAnswer;
    }
  };

  const getScore = () => {
    let correct = 0;
    questions.forEach(question => {
      if (isCorrectAnswer(question, answers[question.id])) {
        correct++;
      }
    });
    return { correct, total: questions.length };
  };

  const score = submitted ? getScore() : null;

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <div className="mb-6">
        <h2 className="text-3xl font-bold text-gray-900 mb-2">小测练习</h2>
        <p className="text-gray-600">请仔细阅读题目并选择正确答案</p>
      </div>

      {submitted && (
        <Card className="mb-6 border-blue-200 bg-blue-50">
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <CheckCircle className="w-5 h-5 text-green-500" />
              <span className="font-medium">
                测试完成！得分：{score.correct}/{score.total} ({Math.round(score.correct / score.total * 100)}%)
              </span>
            </div>
          </CardContent>
        </Card>
      )}

      <div className="space-y-6">
        {questions.map((question, index) => {
          const userAnswer = answers[question.id];
          const isCorrect = submitted ? isCorrectAnswer(question, userAnswer) : null;

          return (
            <Card key={question.id} className={submitted ? (isCorrect ? 'border-green-200' : 'border-red-200') : ''}>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <span className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm font-medium">
                    第 {index + 1} 题
                  </span>
                  {submitted && (
                    isCorrect ? 
                      <CheckCircle className="w-5 h-5 text-green-500" /> : 
                      <XCircle className="w-5 h-5 text-red-500" />
                  )}
                </CardTitle>
                <p className="text-lg text-gray-800 mt-2">{question.question}</p>
                {question.type === 'multiple' && (
                  <p className="text-sm text-gray-500">（多选题）</p>
                )}
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {question.options.map((option) => {
                    const isSelected = question.type === 'multiple' 
                      ? (userAnswer || []).includes(option.id)
                      : userAnswer === option.id;

                    const isCorrectOption = question.type === 'multiple'
                      ? question.correctAnswer.includes(option.id)
                      : question.correctAnswer === option.id;

                    let optionClass = 'p-3 border rounded-lg cursor-pointer transition-colors ';

                    if (submitted) {
                      if (isCorrectOption) {
                        optionClass += 'bg-green-100 border-green-300 text-green-800';
                      } else if (isSelected && !isCorrectOption) {
                        optionClass += 'bg-red-100 border-red-300 text-red-800';
                      } else {
                        optionClass += 'bg-gray-50 border-gray-200 text-gray-600';
                      }
                    } else {
                      optionClass += isSelected 
                        ? 'bg-blue-100 border-blue-300 text-blue-800' 
                        : 'bg-white border-gray-200 hover:bg-gray-50';
                    }

                    return (
                      <div
                        key={option.id}
                        className={optionClass}
                        onClick={() => handleAnswerChange(question.id, option.id, question.type === 'multiple')}
                      >
                        <div className="flex items-center space-x-3">
                          <div className={`w-4 h-4 rounded ${question.type === 'multiple' ? 'rounded-sm' : 'rounded-full'} border-2 flex items-center justify-center`}>
                            {isSelected && (
                              <div className={`w-2 h-2 bg-current ${question.type === 'multiple' ? 'rounded-sm' : 'rounded-full'}`} />
                            )}
                          </div>
                          <span className="font-medium">{option.id.toUpperCase()}.</span>
                          <span>{option.text}</span>
                        </div>
                      </div>
                    );
                  })}
                </div>

                {submitted && (
                  <div className="mt-4 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
                    <h4 className="font-medium text-yellow-800 mb-2">解析：</h4>
                    <p className="text-yellow-700">{question.explanation}</p>
                  </div>
                )}
              </CardContent>
            </Card>
          );
        })}
      </div>

      {!submitted && (
        <div className="mt-8 flex justify-center">
          <Button 
            onClick={handleSubmit}
            size="lg"
            className="px-8"
            disabled={Object.keys(answers).length === 0}
          >
            提交答案
          </Button>
        </div>
      )}

      {submitted && (
        <div className="mt-8 flex justify-center">
          <Button 
            onClick={() => {
              setAnswers({});
              setSubmitted(false);
            }}
            variant="outline"
            size="lg"
            className="px-8"
          >
            重新测试
          </Button>
        </div>
      )}
    </div>
  );
};

export default QuizPage;
